def map_to_memory(chain_response_schema: list) -> dict[str, str]:
  memory_dict = {}
  for item in chain_response_schema:
    key = item.get('name', None)
    value = item.get('answer', None)
    if key is not None and value is not None:
      memory_dict[key] = value

  return memory_dict
