def conditional_function(chain_output: str, inputs:dict) -> dict:
  '''Conditional Function for determining which chain to run next'''

  def upper_snake_case(string:str) -> str:
    return '_'.join(string.upper().split())

  route_vars = inputs.get('ROUTES_metadata', [])
  group_vars = inputs.get('GROUPS_metadata', [])
  chain_output = chain_output.strip()
  selected_groups = [group for group in group_vars if group['name'] == chain_output]

  if len(selected_groups) == 0:
    return {
      'destination': "text_response",
    }
  else:
    selected_group = selected_groups[0]

    observation = ""
    for route in route_vars:
      if not route.get('enabled', True):
        continue
      if route['group'] != selected_group['name']:
        continue

      intent_str = f"intent: '{route['name']}' | agent: [{route['type']}::{upper_snake_case(route['name'])}]"
      if route['description']:
        intent_str = intent_str + f"\n\tdesc:{route['description']}"

      observation = observation + intent_str + "\n-\n"

    return {
      'destination':'route_response', 
      'next_inputs': {
        'observation':observation
      }
    }

def conditional_function(chain_output: str, inputs:dict) -> str:

  if 'KNOWLEDGE::' not in chain_output:
    # Note the exit true
    return {'destination': 'knowledge', 'next_inputs':{'input': chain_output}, "exit": True}

  user_message = inputs['input']
  #generated_route = ""
  #query_message = user_message
  query_message = chain_output.split("KNOWLEDGE::")[1].split("]")[0].replace("_", " ").lower()

  return {'destination': 'knowledge', 'next_inputs':{'input': user_message}, 'updated_memory_variables': {'query_input': query_message}}
